import React, { Component } from 'react'
import { ListGroup,ListGroupItem } from 'reactstrap'

export default class Category extends Component {
   state = {
        categories:[
            {categoryId:1,categoryName:"giyim"},
            {categoryId:2,categoryName:"mobilya"},
            {categoryId:3,categoryName:"teknoloji"},
        ],
        currentCategory :""
    }
  render() {
    return (
        <div class="col-6">
        <h3>Category List</h3>
        <input name='isim' type='text' class="form-control"/>
            <ListGroup>
            {this.state.categories.map(category=>(
                <ListGroupItem><button onClick={()=>this.setState({currentCategory:category.categoryName})} key={category.categoryId} class="btn btn-primary">{category.categoryName}</button></ListGroupItem>
            ))}
            </ListGroup>
            <h4>{this.state.currentCategory}</h4>
        </div>
    )
  }
}
